async function findData(array, filter, max, keys) {
	if (!keys) keys = Object.keys(filter)

	const filtered = []

	let i = 0

	filter:
	while (i < array.length) {
		for (const key of keys) {
			if (filter[key] !== array[i][key]) {
				i++

				continue filter
			}
		}

		if (i > max) break

		filtered.push(array[i])

		i++
	}
  
	return filtered
}

module.exports = findData