const addReactions = async d => {
  
  const code = d.command.code
  
  if (code.split("$addReactions").length >= 3) return d.message.channel.send(`❌ Can't use more than one $addReactions `)
  
  const inside = code.split(`$addReactions[`)[1].split("]")[0]
  
  return {
    code: code.replaceLast(`$addReactions[${inside}]`, ""),
    reactions: inside.split(";")
  }}

module.exports = addReactions 