const addTimestamp = d => {
    
  const code = d.command.code 
  
  const r = d.command.code.split("$addTimestamp").length - 1
  
  if (r >= 3) return d.message.channel.send(`❌ Can't use more than one $addTimestamp `)
  
  
  const after = code.split("$addTimestamp")[r]
  
  if (after.check() === "[") {
      const ms = after.split("[")[1].split("]")[0] 
      
      const anything = after.split("]")[0] 
      
      return {
          code: code.replaceLast(`$addTimestamp${anything}]`, ""), 
          embed: d.embed.setTimestamp(Number(ms) || 0)
      }
  }else {
      return {
          code: code.replaceLast(`$addTimestamp`, ""), 
          embed: d.embed.setTimestamp()
      }
  }
}

module.exports = addTimestamp 