module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$allChannelsCount").length - 1 
    
    const after = code.split("$allChannelsCount")[r]
    
    if (after.startsWith("[")) {
        const types = after.slice(1).split ("]") [0].split(";") 
        return {
            code: code.replaceLast(`$allChannelsCount[${types.join(";")}]`, d.client.channels.cache.filter(ch => types.includes(ch.type)).size)
        }
    } else {
        return {
            code: code.replaceLast(`$allChannelsCount`, d.client.channels.cache.size)
        }
    }
}