const author = d => {

  const r = d.command.code.split("$author[").length
  
  if (r >= 3) return d.message.channel.send(`❌ Can't use more than one $author.`)
  
  const inside = d.command.code.split("$author[")[1].split("]")[0]
  
  const [ text, url ] = inside.split(";")

  return {
    code: d.command.code.replaceLast(`$author[${inside}]`, ""),
    embed: d.embed.setAuthor(text.addBrackets(), url) 
  }
}

module.exports = author