const embed = require("../../handlers/errors")

module.exports = async d => {
    const code = d.command.code

    const r = code.split("$blackListRoleIDs[").length - 1

    const inside = code.split("$blackListRoleIDs[")[r].split("]")[0]

    const fields = inside.split(";")

    const errorMsg = fields.pop()

    if (fields.some(id => message.member.roles.cache.has(id))) return embed(d, errorMsg)

    return {
        code: code.replaceLast(`$blackListRoleIDs[${inside}]`, "")
    }
}