const botTyping = (d) => {
  
    let code = d.command.code   
     
    d.message.channel.startTyping()

    setTimeout(() => {
        d.message.channel.stopTyping()    
    }, 5000);

    code = code.replaceLast(`$botTyping`, "")
    return {
        code: code
    }
}

module.exports = botTyping