module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$channelCategoryID").length - 1 
    
    const after = code.split("$channelCategoryID")[r] 
    
    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0] 
        
        const channel = d.message.guild.channels.cache. get(inside) 
        
        if (!channel) return d.error(`‚ùå Invalid channel ID in \`$channelCategoryID[${inside}]\``) 
        
        return {
            code: code.replaceLast(`$channelCategoryID[${inside}]`, channel.parentID || "")
        }
    } else {
        return {
            code: code.replaceLast(`$channelCategoryID`, d.message.channel.parentID || "")
        }
    }
}