module.exports = async d => {
    const code = d.command.code

    const r = code.split("$channelCount").length - 1

    const after = code.split("$channelCount")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        return {
            code: code.replaceLast(`$channelCount[${inside}]`, d.message.guild.channels.cache.filter(c => inside.split(";").includes(c.type)).size)
        }
    } else {
        return {
            code: code.replaceLast(`$channelCount`, d.message.guild.channels.cache.size)
        }
    }
}