module.exports = d => {

  const code = d.command.code
  
  const r = code.split("$channelName").length - 1
  
  const after = code.split("$channelName")[r]
  
  //this one is more complex
  if (after.startsWith("[")) {

    const id = after.split("[")[1].split("]")[0]
    
    const channel = d.message.guild.channels.cache.get(id.addBrackets())
    
    if (!channel) return d.error(`‚ùå Invalid channel ID in \`$channelName[${id}]\``) 
    
    return {
      code: code.replaceLast(`$channelName[${id}]`, channel.name)
    } 
  } else {
    return {
      code: code.replaceLast("$channelName", d.message.channel.name)
    } 
  } 
} 