const embed = require("../../handlers/errors.js")

module.exports = async d => {
    const code = d.command.code

    const r = code.split("$channelSendMessage[").length - 1

    const inside = code.split("$channelSendMessage[")[r].split("]")[0]

    const fields = inside.split(";")
    
    const [channelID, text, returnID = "no"] = fields 

    const msg = await embed(d, text, true)

    const channel = d.client.channels.cache.get(channelID)

    if (!channel) return d.error(`:x: Invalid channel ID in \`$channelSendMessage[${inside}]\``)

    const m = await channel.send(msg.message, msg.embed).catch(err => {})

    if (!m) return d.error(`:x: Failed to send message!`)

    if (msg.reactions) {
        for (const r of msg.reactions) {
            await m.react(r).catch(err => null)
        }
    }
    
    return {
        code: code.replaceLast(`$channelSendMessage[${inside}]`, returnID === "yes" ? m.id : "") 
    }
}