module.exports = async d => {
    const code = d.command.code

    const r = code.split("$channelTopic").length - 1

    const after = code.split("$channelTopic")[r].split("]")[0]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const channel = d.message.guild.channels.cache.get(inside)

        if (!channel) return d.error(`:x: Invalid channel ID in \`$channelTopic[${inside}]\``)

        return {
            code: code.replaceLast(`$channelTopic[${inside}]`, (channel.topic || "none").removeBrackets())
        }
    } else {
        return {
            code: code.replaceLast(`$channelTopic`, (d.message.channel.topic || "none").removeBrackets())
        }
    }
}