module.exports = async d => {
    const code = d.command.code

    const r = code.split("$channelType").length - 1

    const after = code.split("$channelType")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const channel = d.client.channels.cache.get(inside)

        if (!channel) return d.error(`:x: Invalid channel ID in \`$channelType[${inside}]\``)

        return {
            code: code.replaceLast(`$channelType[${inside}]`, channel.type)
        }
    } else {
        return {
            code: code.replaceLast(`$channelType`, d.message.channel.type)
        }
    }
}