module.exports = async d => {
    const code = d.command.code

    const r = code.split("$checkContains[").length - 1

    const inside = code.split("$checkContains[")[r].split("]")[0]

    const fields = inside.split(";")

    const msg = fields.shift().addBrackets()

    return {
        code: code.replaceLast(`$checkContains[${inside}]`, fields.some(field => msg.includes(field.addBrackets())))
    }
}