const description = d => {
  
  const r = d.command.code.split("$description").length
  
  if (r >= 3) return d.message.channel.send(`‚ùå Can't use more than one $description.`)
  
  const inside = d.command.code.split("$description[")[1].split("]")[0]
  
  return {
    code: d.command.code.replaceLast(`$description[${inside}]`, ""),
    embed: d.embed.setDescription(inside.addBrackets()) 
  }
}

module.exports = description 