module.exports = async d => {
    
    const { channel, message, data, error, command, embed, array, randoms, client} = d 
    
    const code = d.command.code
    
    const inside = code.split("$djsEval[")[1].split("]")[0]
    
    const fields = inside.split(";") 
    
    let OUTPUT = false 
    
    if (fields[fields.length - 1] === "yes") {
        OUTPUT = "yes" 
        fields.pop()
    }
    
    let CODE = fields.join(";")
    try {
        var evaled = eval(CODE.addBrackets())    
    } catch (error) {
        d.error(error.message + ` in \`$djsEval[${inside}]\``)
    }

    return {
        code: d.command.code.replaceLast(`$djsEval[${inside}]`, OUTPUT === "yes" ? typeof evaled === "object" ? require("util").inspect(evaled, {depth:0}).deleteBrackets() : typeof evaled === "string" ? evaled.deleteBrackets() : evaled : "")
    }
}