module.exports = async d => {
    const code = d.command.code

    const r = code.split("$filterMessage[").length - 1

    const inside = code.split("$filterMessage[")[r].split("]")[0]

    let [
        text, 
        letters
    ] = inside.split(";")

    for (const letter of letters.split("")) {
        text = text.split(letter).join("")
    }

    return {
        code: code.replaceLast(`$filterMessage[${inside}]`, text)
    }
}