module.exports = d => {
  //gay const = dm.user.client.message(stfu);
  const code = d.command.code
  
  const r = code.split("$findChannel[").length - 1
  
  const option = code.split("$findChannel[")[r].split("]")[0]
  
  const manager = d.client.channels.cache
  
  const c = manager.get(option) || manager.find(c => c.name.toLowerCase() === option.toLowerCase()) || d.message.mentions.channels.first() || d.message.channel
  
  return {
    code: code.replaceLast(`$findChannel[${option}]`, c.id)
  } 
} 