module.exports = async (d) => {
  //stfu
  
  const code = d.command.code
  
  const r = code.split("$findMember[").length - 1
  
  const option = code.split("$findMember[")[r].split("]")[0]
  
  const client = d.client
  
  const u = d.message.guild.members.cache.get(option) || d.message.guild.members.cache.find(m => m.user.tag.toLowerCase() === option.toLowerCase() || m.user.username.toLowerCase() === option.toLowerCase() || m.displayName.toLowerCase() === option.toLowerCase()) || d.message.mentions.members.first() || d.message.member
  
  return{
    code: code.replaceLast(`$findMember[${option}]`, u.user.id)
  } 
} 