module.exports = async d => {
    const code = d.command.code

    const r= code.split("$findTextSplitIndex[").length - 1

    const inside = code.split("$findTextSplitIndex[")[r].split("]")[0]

    return {
        code: code.replaceLast(`$findTextSplitIndex[${inside}]`, d.array.findIndex(value => value === inside) + 1)
    }
}