module.exports = async (d) => {
  //stfu
  
  const code = d.command.code
  
  const r = code.split("$findUser[").length - 1
  
  const option = code.split("$findUser[")[r].split("]")[0]
  
  const client = d.client
  
  let u = d.client.users.cache.get(option) || d.message.mentions.users.first() || d.client.users.cache.find(m => m.tag.toLowerCase() === option.addBrackets().toLowerCase() || m.username.addBrackets().toLowerCase() === option.toLowerCase()) || d.message.author
  
  if (u.id === d.message.author.id) {
    u = await d.client.users.fetch(option).catch(err => d.message.author)
  }
  
  return{
    code: code.replaceLast(`$findUser[${option}]`, u.id)
  } 
} 