const footer = d => {

  const r = d.command.code.split("$footer").length
  
  if (r >= 3) return d.message.channel.send(`❌ Can't use more than one $footer. `)
  
  const inside = d.command.code.split("$footer[")[1].split("]")[0]
  
  const [ text, url ] = inside.split(";")

  return {
    code: d.command.code.replaceLast(`$footer[${inside}]`, ""),
    embed: d.embed.setFooter(text.addBrackets(), url) 
  }
}

module.exports = footer