
module.exports = async d => {
    const code = d.command.code

    const r = code.split("$getCustomStatus").length - 1

    const after = code.split("$getCustomStatus")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]
        
        const [id, option = "state"] = inside.split(";") 
        
        const user = await d.client.users.fetch(id).catch(err => { })

        if (!user) return d.error(`:x: Invalid user ID in \`$getCustomStatus[${inside}]\``)

        const activity = user.presence.activities.find(c => c.name === "Custom Status")

        return {
            code: code.replaceLast(`$getCustomStatus[${inside}]`, activity ? ((option === "state" ? activity.state : activity.emoji ? activity.emoji.toString() : "none") || "none"). removeBrackets() : "none")
        }
    } else {
        const activity = d.message.author.presence.activities.find(c => c.name === "Custom Status")

        return {
            code: code.replaceLast(`$getCustomStatus`, activity ? activity.state ? activity.state.removeBrackets() : "none" : "none")
        }
    }
}