
module.exports = async d => {

    const code = d.command.code

    const r = code.split("$getGlobalUserVar[").length - 1

    const inside = code.split("$getGlobalUserVar[")[r].split("]")[0]

    const [ variable, userID = d.message.author.id ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found!`)

    let item = await d.client.db.findOne({
            variable: variable,
            varID: `${userID}`
    }) 
    
    if (!item) {
        item = d.client.variables[variable]
    } else {
        item = item.value || d.client.variables[variable]
    }

    return {
        code: code.replaceLast(`$getGlobalUserVar[${inside}]`, item)
    }
}