
const getMessageVar = async d => {

    const code = d.command.code

    const r = code.split("$getMessageVar").length - 1

    const inside = code.split("$getMessageVar[")[r].split("]")[0]

    const [ variable, messageID = d.message.id ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found!`)

    let item = await d.client.db.findOne({
            variable: variable,
            varID: `${messageID}`
    }) 

    if (!item) {
        item = d.client.variables[variable]
    } else {
        item = item.value || d.client.variables[variable]
    }

    return {
        code: code.replaceLast(`$getMessageVar[${inside}]`, item)
    }
}

module.exports = getMessageVar