const getVar = async d => {

    const code = d.command.code

    const r = code.split("$getVar").length - 1

    const inside = code.split("$getVar[")[r].split("]")[0]

    const [ variable ] = inside.split(";")

    if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found!`)

    let item = await d.client.db.findOne({
            variable: variable,
            varID: `0`
    }) 

    if (!item) {
        item = d.client.variables[variable]
    } else {
        item = item.value || d.client.variables[variable]
    }

    return {
        code: code.replaceLast(`$getVar[${inside}]`, item)
    }
}

module.exports = getVar