module.exports = async d => {

    const code = d.command.code

    const r = code.split("$globalUserLeaderboard[").length - 1

    const inside = code.split("$globalUserLeaderboard[")[r].split("]")[0]

    const [
        variable,
        type = "asc",
        custom = `{top}.- {username} - {value}`,
        list = 10,
        page = 1
    ] = inside.split(";")

    const all = await d.client.db.find({
            variable: variable
            //ruben isnt gay
    })

    let y = 0

    let content = []

    for (const data of all.filter(e => d.client.guilds.cache.get(e.varID) === undefined).sort((x, y) => Number(y.value) - Number(x.value))) {
        const user = await d.client.users.fetch(data.varID).catch(err => {})

        if (user) {
            y++

            let text = custom.replace(`{top}`, y).replace("{id}", user.id).replace(`{username}`, user.username.removeBrackets()).replace(`{value}`, Number(data.value))
            
            if (text.includes("{execute:")) {
                let ins = text.split("{execute:")[1].split("}")[0] 
                
                const awaited = d.client.awaited_commands.find(c =>c.name ===ins) 
                
                if (!awaited) return d.error(`‚ùå Invalid awaited command '${ins}' in \`$globalUserLeaderboard[${inside}]\``) 
                
                const CODE = await d.interpreter(d.client, {
                    guild: d.message.guild, 
                    channel: d.message.channel, 
                    author: user
                }, d.args, awaited, undefined, true) 
                
                text = text.replace(`{execute:${ins}}`, CODE)
            }
            
            content.push(text)
        }
    }
    
    if (type === "desc") content = content.reverse()

    const px = page * list - list, py = page * list

    return {
        code: code.replaceLast(`$globalUserLeaderboard[${inside}]`, content.slice(px, py).join("\n"))
    }
    //yeah
//it so longg
}