module.exports = async d => {

    const code = d.command.code

    const r = code.split("$guildID").length - 1

    const after = code.split("$guildID")[r]

    if (after.startsWith("[")) {
        const name = after.slice(1).split("]")[0]

        const guild = d.client.guilds.cache.find(g => g.name === name.addBrackets())

        if (!guild) return d.error(`:x: Invalid guild name in \`$guildID[${name}]\``)

        return {
            code: code.replaceLast(`$guildID[${name}]`, guild.id)
        }
    } else {
        return {
            code: code.replaceLast(`$guildID`, d.message.guild.id)
        }
    }
}