module.exports = async d => {
    const code = d.command.code

    const r = code.split("$highestRole").length - 1

    const after = code.split("$highestRole")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const member = await d.message.guild.members.fetch(inside).catch(err => {})

        if (!member) return d.error(`:x: Invalid user ID in \`$highestRole[${inside}]\``)

        else return {
            code: code.replaceLast(`$highestRole[${inside}]`, member.roles.highest.id)
        }
    } else {
        return {
            code: code.replaceLast(`$highestRole`, d.message.member.roles.highest.id)
        }
    }
}