module.exports = async d => {
    const code = d.command.code

    const r = code.split("$isBanned[").length - 1

    const inside = code.split("$isBanned[")[r].split("]")[0]

    const user = await d.client.users.fetch(inside).catch(err => {})

    if (!user) return d.error(`:x: Invalid user ID in \`$isBanned[${inside}]\``)

    const ban = await d.message.guild.fetchBan(inside).catch(err => {})

    return {
        code: code.replaceLast(`$isBanned[${inside}]`, ban ? true : false)
    }
}