module.exports = async d => {
    const code = d.command.code

    const r = code.split("$isBoosting").length - 1

    const after = code.split("$isBoosting")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const member = await d.message.guild.members.fetch(inside).catch(err => {})

        if (!member) return d.error(`:x: Invalid user ID in \`$isBoosting[${inside}]\``)

        return {
            code: code.replaceLast(`$isBoosting[${inside}]`, member.premiumSinceTimestamp !== 0)
        }
    } else {
        return {
            code: code.replaceLast(`$isBoosting`, d.message.member.premiumSinceTimestamp !== 0)
        }
    }
}