module.exports = async d => {
    const code = d.command.code

    const r = code.split("$isBot").length - 1

    const after = code.split("$isBot")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const user = await d.client.users.fetch(inside).catch(Err =>{})

        if (!user) return d.error(`:x: Invalid user ID in \`$isBot[${inside}]\``)

        return {
            code: code.replaceLast(`$isBot[${inside}]`, user.bot)
        }
    } else {
        return {
            code: code.replaceLast(`$isBot`, d.message.author.bot)
        }
    }
}