module.exports = async d => {
    const code = d.command.code
    
    const r = code.split("$isDeafened[").length - 1
    
    const inside = code.split("$isDeafened[")[r].split("]")[0]
    
    const user = await d.message.guild.members.fetch(inside).catch(err => {})
    
    if (!user) return d.error(`âŒ Invalid user ID in \`$isDeafened[${inside}]\``)
        
    return {
        code: code.replaceLast(`$isDeafened[${inside}]`, user.voice.selfDeaf)
    }
}  