module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$isHoisted[").length - 1 
    
    const inside = code.split("$isHoisted[")[r].split("]")[0] 
    
    const role = d.message.guild.roles.cache.get(inside) 
    
    if (!role) return d.error(`‚ùå Invalid role ID in \`$isHoisted[${inside}]\``) 
    
    return {
        code: code.replaceLast(`$isHoisted[${inside}]`, role.hoist)
    }
}