module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$isMentionable[").length - 1 
    
    const inside = code.split("$isMentionable[")[r].split("]")[0] 
    
    const role = d.message.guild.roles.cache.get(inside) 
    
    if (!role) return d.error(`‚ùå Invalid role ID in \`$isMentionable[${inside}]\``) 
    
    return {
        code: code.replaceLast(`$isMentionable[${inside}]`, role.mentionable)
    }
}