module.exports = async d => {
    const code = d.command.code
    
    const r = code.split ("$isMentioned[").length - 1
    
    const option = code.split("$isMentioned[")[r].split ("]")[0]
    
    const mentions =d.message.mentions 
    
    return {
        code: code.replaceLast(`$isMentioned[${option}]`, option === "everyone" ? mentions.everyone : (mentions.roles.has(option) || mentions.channels.has(option) || mentions.users.has(option)))
    }
}