module.exports = async d => {
    const code = d.command.code
    
    const r = code.split("$isMuted[").length - 1
    
    const inside = code.split("$isMuted[")[r].split("]")[0]
    
    const user = await d.message.guild.members.fetch(inside).catch(err => {})
    
    if (!user) return d.error(`❌ Invalid user ID in \`$isDeafened[${inside}]\``)
        
    return {
        code: code.replaceLast(`$isMuted[${inside}]`, user.voice.selfMute)
    }
}  