module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$isUserDMEnabled").length - 1 
    
    const after = code.split("$isUserDMEnabled")[r].split("]")[0] 
    
    if (after[0] === "[") {
        const id = after.slice(1).split("]")[0] 
        
        const user = await d.client.users.fetch(id).catch(err => null) 
        
        if (!user) return d.error(`❌ Invalid user ID in \`$isUserDMEnabled[${id}]\``) 
        
        const c = await user.send("").catch(err => err.code)
        
        return {
            code: code.replaceLast(`$isUserDMEnabled[${id}]`, c === 50007 ? false : true)
        }
    } else {
        const c = await d.message.author.send("").catch(err => err.code) 
        
        return {
            code: code.replaceLast(`$isUserDMEnabled`, c === 50007? false : true)
        }
    }
}