const Discord = require("discord.js") 

module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$isValidHex[").length - 1 
    
    const inside = code.split("$isValidHex[")[r].split("]")[0] 
    
    const test = new Discord.MessageEmbed()
    
    let response = true 
    
    try {
        test.setColor(inside) 
        
        if (!test.color) response = false 
    } catch (err) {
        response = false 
    }
    
    return {
        code: code.replaceLast(`$isValidHex[${inside}]`, response)
    }
}