const axios = require('axios');

module.exports = async d => {
	const code = d.command.code;

	const r = code.split(`$isValidLink[`).length - 1
	
	const inside = code.split(`$isValidLink[`)[r].split("]")[0] 
	
	let bool = await axios.get(inside.addBrackets()).catch(err => null) 
	
	return {
	    code: code.replaceLast(`$isValidLink[${inside}]`, bool ? true : false)
	}
};
