module.exports = async d => {
    const code = d.command.code

    const r = code.split("$joinSplitText[").length - 1

    const inside = code.split("$joinSplitText[")[r].split("]")[0]
    
    return {
        code: code.replaceLast(`$joinSplitText[${inside}]`, d.array.join(inside).removeBrackets())
    }
}