const mentionedChannels = d => {
  
  let code = d.command.code
  
  const r = code.split("$mentionedChannels[").length - 1
  
  const inside = code.split("$mentionedChannels[")[r].split("]")[0]
  
  const [ mention, returnChannel = "no" ] = inside.split(";")
  
  if (isNaN(mention)) return d.error(`‚ùå Invalid mention number in \`$mentionedChannels[${inside}]\``)
  
  const channel = d.message.mentions.channels.array()[Number(mention) - 1]
  
  return {
    code: code.replaceLast(`$mentionedChannels[${inside}]`, channel ? channel.id : returnChannel === "yes" ? d.message.channel.id : "") 
  } 
}

module.exports = mentionedChannels 