const message = d => {
  
  let code = d.command.code
  
  const r = code.split("$message").length - 1
  
  const after = code.split("$message")[r]
  
  if (after.startsWith("[")) {
    const n = after.split("[")[1].split("]")[0]
    
    if (isNaN(n)) return d.error(`❌ Invalid number in \`$message[${n}]\``)
    
    code = code.replaceLast(`$message[${n}]`, d.args[Number(n) - 1] ? d.args[Number(n) - 1].deleteBrackets() : "")
  } else {
    code = code.replaceLast("$message", d.args.join(" ").deleteBrackets()) 
  }
  
  return {
    code: code 
  } 
}
module.exports = message