module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$messageExists[").length - 1
    
    const inside = code.split("$messageExists[")[r].split("]")[0]
    
    const [channelID, messageID] = inside.split(";")
    
    const channel = d.client.channels.cache.get(channelID)
    
    if (!channel) return d.error(`❌ Invalid channel ID in \`$messageExists[${inside}]\``)
        
    const msg = await channel.messages.fetch(messageID).catch(err => null)

    return {
        code: code.replaceLast(`$messageExists[${inside}]`, msg ? true : false)
    }
}


