const noEscapingMessage = d => {
  
  let code = d.command.code
  
  const r = code.split("$noEscapingMessage").length - 1
  
  const after = code.split("$noEscapingMessage")[r]
  
  if (after.startsWith("[")) {
    const n = after.split("[")[1].split("]")[0]
    
    if (isNaN(n)) return d.error(`‚ùå Invalid number in \`$noEscapingMessage[${n}]\``)
    
    code = code.replaceLast(`$noEscapingMessage[${n}]`, d.args[Number(n) - 1] ? d.args[Number(n) - 1] : "")
  } else {
    code = code.replaceLast("$noEscapingMessage", d.args.join(" ")) 
  }
  
  return {
    code: code 
  } 
}
module.exports = noEscapingMessage