module.exports = async d => {
    const code = d.command.code

    const r = code.split("$numberSeparator[").length - 1

    const inside = code.split("$numberSeparator[")[r].split("]")[0]

    const [
        number,
        separator
    ] = inside.split(";")

    const n = Number(number)

    if (!n && n !== 0) return d.error(`:x: Invalid number in \`$numberSeparator[${inside}]\``)

    return {
        code: code.replaceLast(`$numberSeparator[${inside}]`, !separator ? n.toLocaleString() : n.toLocaleString().split(",").join(separator))
    }
}