const channelOptions = require("../../utils/channelOptions") 

module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$oldChannel" ).length - 1 
    
    const inside = code.split("$oldChannel[")[r].split("]")[0] 
    
    const option = Object.keys(channelOptions).find(opt => opt === inside) 
    
    if (!option) return d.error(`‚ùå Invalid option in \`$oldChannel[${inside}]\``) 
    
    const executor = channelOptions[option].split(";")[1] 
    
    return {
        code: code.replaceLast(`$oldChannel[${inside}]`, d.data.old_channel ? eval(`d.data.old_channel${executor}`) : "")
    }
}