const presenceOptions = require("../../utils/presenceOptions") 

module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$oldPresence").length - 1 
    
    const inside = code.split("$oldPresence[")[r].split("]")[0] 
    
    const option = Object.keys(presenceOptions).find(opt => opt === inside) 
    
    if (!option) return d.error(`‚ùå Invalid option in \`$oldPresence[${inside}]\``) 
    
    const executor = presenceOptions[option].split(";")[1] 
    
    return {
        code: code.replaceLast(`$oldPresence[${inside}]`, d.data.presence ? eval(`d.data.presence${executor}`) : "")
    }
}