const roleOptions = require("../../utils/roleOptions") 

module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$oldRole").length - 1 
    
    const inside = code.split("$oldRole[")[r].split("]")[0] 
    
    const option = Object.keys(roleOptions).find(opt => opt === inside) 
    
    if (!option) return d.error(`‚ùå Invalid option in \`$oldRole[${inside}]\``) 
    
    const executor = roleOptions[option].split(";")[1] 
    
    return {
        code: code.replaceLast(`$oldRole[${inside}]`, d.data.old_role ? eval(`d.data.old_role${executor}`) : "")
    }
}