const embed = require("../../handlers/errors")

module.exports = async d => {
    const code = d.command.code

    const r = code.split("$onlyNSFW[").length - 1

    const inside = code.split("$onlyNSFW[")[r].split("]")[0]

    if (!d.message.channel.nsfw) return embed(d, inside)

    return {
        code: code.replaceLast(`$onlyNSFW[${inside}]`, "")
    }
}